}

contracts/CartCostFunctions.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {FunctionsClient} from "@chainlink/contracts/src/v0.8/functions/v1_0_0/FunctionsClient.sol";
import {FunctionsRequest} from "@chainlink/contracts/src/v0.8/functions/v1_0_0/libraries/FunctionsRequest.sol";

contract CartCostFunctions is FunctionsClient {
    using FunctionsRequest for FunctionsRequest.Request;

    bytes32 public lastRequestId;
    bytes public lastResponse;
    bytes public lastError;

    struct RequestStatus {
        bool fulfilled;
        bool exists;
        bytes response;
        bytes err;
        string functionToCall;
    }
    mapping(bytes32 => RequestStatus) public requests;
    bytes32[] public requestIds;

    event Response(
        bytes32 indexed requestId,
        string data,
        bytes response,
        bytes err
    );

    address router = 0xb83E47C2bC239B3bf370bc41e1459A34b41238D0;
    bytes32 donID = 0x66756e2d657468657265756d2d7365706f6c69612d3100000000000000000000;
    uint32 gasLimit = 300000;
    uint64 public subscriptionId;

    string public source =
        "const apiResponse = await Functions.makeHttpRequest({"
        "url: `https://9172-213-214-42-42.ngrok-free.app/stats`,"
        "responseType: 'json'"
        "});"
        "if (apiResponse.error) {"
        "throw Error('Request failed');"
        "}"
        "const { cartLineCostSum, productViewedCount, checkoutCompletedCount, totalTransactionAmount } = apiResponse.data;"
        "const functionToCall = args[0];"
        "switch(functionToCall) {"
        "  case 'cartLineCostSum':"
        "    return Functions.encodeString(cartLineCostSum.toString());"
        "  case 'productViewedCount':"
        "    return Functions.encodeString(productViewedCount.toString());"
        "  case 'checkoutCompletedCount':"
        "    return Functions.encodeString(checkoutCompletedCount.toString());"
        "  case 'totalTransactionAmount':"
        "    return Functions.encodeString(totalTransactionAmount.toString());"
        "  default:"
        "    throw Error('Invalid function call');"
        "}";

    struct StatsStruct {
        string cartLineCostSum;
        string productViewedCount;
        string checkoutCompletedCount;
        string totalTransactionAmount;
        uint timestamp;
    }
    StatsStruct public stats;

    constructor(uint64 functionsSubscriptionId) FunctionsClient(router) {
        subscriptionId = functionsSubscriptionId;
    }

    function requestStat(string memory functionToCall) external returns (bytes32 requestId) {
        string[] memory args = new string[](1);
        args[0] = functionToCall;

        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(source);
        req.setArgs(args);

        lastRequestId = _sendRequest(
            req.encodeCBOR(),
            subscriptionId,
            gasLimit,
            donID
        );

        requests[lastRequestId] = RequestStatus({
            exists: true,
            fulfilled: false,
            response: "",
            err: "",
            functionToCall: functionToCall
        });
        requestIds.push(lastRequestId);

        return lastRequestId;
    }

    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        require(requests[requestId].exists, "request not found");

        lastError = err;
        lastResponse = response;

        requests[requestId].fulfilled = true;
        requests[requestId].response = response;
        requests[requestId].err = err;

        string memory data = string(response);
        string memory functionToCall = requests[requestId].functionToCall;

        if (compareStrings(functionToCall, "cartLineCostSum")) {
            stats.cartLineCostSum = data;
        } else if (compareStrings(functionToCall, "productViewedCount")) {
            stats.productViewedCount = data;
        } else if (compareStrings(functionToCall, "checkoutCompletedCount")) {
            stats.checkoutCompletedCount = data;
        } else if (compareStrings(functionToCall, "totalTransactionAmount")) {
            stats.totalTransactionAmount = data;
        }

        stats.timestamp = block.timestamp;

        emit Response(requestId, data, lastResponse, lastError);
    }

    function compareStrings(string memory a, string memory b) internal pure returns (bool) {
        return keccak256(bytes(a)) == keccak256(bytes(b));
    }

    function getStats() external view returns (StatsStruct memory) {
        return stats;
    }
}

contracts/EvolvingCheckoutSVG.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// Importing OpenZeppelin contracts
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

// Importing Chainlink contracts
import "./AggregatorV3Interface.sol";

// Importing the CartCostFunctions contract
import "./CartCostFunctions.sol";

contract EvolvingCheckoutSVG is ERC721, ERC721URIStorage {
    using Counters for Counters.Counter;
    using Strings for uint256;

    Counters.Counter public tokenIdCounter;

    // Create price feed
    AggregatorV3Interface internal priceFeed;
    uint256 public lastPrice = 0;

    // Reference to the CartCostFunctions contract
    CartCostFunctions public cartCostFunctions;

    // Set variables for NFT
    string priceIndicatorUp = unicode"😀";
    string priceIndicatorDown = unicode"😔";
    string public priceIndicator;

    address ethusdAddress = 0x694AA1769357215DE4FAC081bf1f309aDC325306;

    constructor(address cartCostFunctionsAddress) ERC721("Benchmark Checkout SVG", "CSVG") {
        priceFeed = AggregatorV3Interface(ethusdAddress);
        cartCostFunctions = CartCostFunctions(cartCostFunctionsAddress);

        // Mint an NFT
        safeMint();
    }

    function safeMint() public {
        uint256 tokenId = tokenIdCounter.current();
        tokenIdCounter.increment();
        _safeMint(msg.sender, tokenId);
        updateSVG(tokenId);
    }

    // Update the SVG
    function updateSVG(uint256 tokenId) public {
        // Create the SVG string
        string memory finalSVG = buildSVG();
        // Base64 encode the SVG
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "Dynamic NFT",',
                        '"description": "Evolving NFT based on checkout sales beating latest benchmark ETH price",',
                        '"image": "data:image/svg+xml;base64,',
                        Base64.encode(bytes(finalSVG)),
                        '"}'
                    )
                )
            )
        );
        // Create token URI
        string memory finalTokenURI = string(
            abi.encodePacked("data:application/json;base64,", json)
        );
        // Set token URI
        _setTokenURI(tokenId, finalTokenURI);
    }

    // Build the SVG string
    function buildSVG() internal view returns (string memory) {
        string memory headSVG = "<svg xmlns='http://www.w3.org/2000/svg' version='1.1' xmlns:xlink='http://www.w3.org/1999/xlink' xmlns:svgjs='http://svgjs.com/svgjs' width='500' height='500' preserveAspectRatio='none' viewBox='0 0 500 500'> <rect width='100%' height='100%' fill='#000000' />";

        // Add stars based on block timestamp
        string memory starsSVG = addStars(block.timestamp);

        string memory bodySVG = string(
            abi.encodePacked(
                "<text x='50%' y='50%' font-size='128' dominant-baseline='middle' text-anchor='middle' fill='white'>",
                compareCheckoutValueWithETHPrice(),
                "</text>"
            )
        );

        // Close SVG
        string memory tailSVG = "</svg>";

        // Concatenate SVG strings
        string memory _finalSVG = string(
            abi.encodePacked(headSVG, starsSVG, bodySVG, tailSVG)
        );
        return _finalSVG;
    }

    // Add stars based on block timestamp
    function addStars(uint256 timestamp) internal pure returns (string memory) {
        string memory starsSVG = "";
        uint256 numStars = timestamp % 100; // Limiting the number of stars for simplicity

        for (uint256 i = 0; i < numStars; i++) {
            uint256 x = (timestamp + i * 37) % 500;
            uint256 y = (timestamp + i * 43) % 500;
            starsSVG = string(
                abi.encodePacked(
                    starsSVG,
                    "<circle cx='", x.toString(), "' cy='", y.toString(), "' r='2' fill='yellow' />"
                )
            );
        }
        return starsSVG;
    }

    // Compare checkout value with ETH price
    function compareCheckoutValueWithETHPrice() public view returns (string memory) {
        uint256 checkoutValue = getCumulativeCheckoutValue();
        uint256 ethPrice = getChainlinkDataFeedLatestAnswer();

        if (checkoutValue > ethPrice) {
            return priceIndicatorUp;
        } else {
            return priceIndicatorDown;
        }
    }

    function getCumulativeCheckoutValue() public view returns (uint256) {
        CartCostFunctions.StatsStruct memory stats = cartCostFunctions.getStats();
        return parseStringToUint(stats.totalTransactionAmount);
    }

    function getChainlinkDataFeedLatestAnswer() public view returns (uint256) {
        (, int256 price, , , ) = priceFeed.latestRoundData();
        return uint256(price);
    }

    function parseStringToUint(string memory numString) internal pure returns (uint256) {
        bytes memory b = bytes(numString);
        uint256 result = 0;
        for (uint i = 0; i < b.length; i++) {
            if (b[i] >= "0" && b[i] <= "9") {
                result = result * 10 + (uint8(b[i]) - 48);
            }
        }
        return result;
    }

    // The following function is an override required by Solidity.
    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public view override(ERC721, ERC721URIStorage) returns (string memory)
    {
        return super.tokenURI(tokenId);
    }
}

contracts/RandomGenerator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// Deploy on Fuji

import {IVRFCoordinatorV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/interfaces/IVRFCoordinatorV2Plus.sol";
import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";


contract RandomGenerator is VRFConsumerBaseV2Plus {

    //VRF
    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);

    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        uint256[] randomWords;
    }
    mapping(uint256 => RequestStatus) public s_requests; /* requestId --> requestStatus */   

    // https://docs.chain.link/vrf/v2/subscription/supported-networks
    // Fuji coordinator switched to sepolia
    // 0x8103B0A8A00be2DDC778e6e7eaa21791Cd364625
    // 0x474e34a077df58807dbe9c96d3c009b23b3c6d0cce433e59bbf5b34f823bc56c
    // https://docs.chain.link/vrf/v2-5/supported-networks#avalanche-fuji-testnet
    IVRFCoordinatorV2Plus COORDINATOR;
    // sepolia 
    address vrfCoordinator = 0x8103B0A8A00be2DDC778e6e7eaa21791Cd364625;
    bytes32 keyHash = 0x474e34a077df58807dbe9c96d3c009b23b3c6d0cce433e59bbf5b34f823bc56c;
    uint32 callbackGasLimit = 2500000;
    uint16 requestConfirmations = 3;
    uint32 numWords =  1;

    // past requests Ids.
    uint256[] public requestIds;
    uint256 public lastRequestId;
    uint256[] public lastRandomWords;

    // Your subscription ID.
    uint256 public s_subscriptionId;

    uint256[] public raffleResult;
    uint256 public maximum = 100;    // Raffle until this number

    constructor(uint256 subscriptionId) 
        VRFConsumerBaseV2Plus(vrfCoordinator) 
    {
        COORDINATOR = IVRFCoordinatorV2Plus(vrfCoordinator);
        s_subscriptionId = subscriptionId;
    }

    function listNumbers() public view returns (uint256[] memory) {
        return raffleResult;
    }

    // Example: I'd like to have 2 numbers (amount) in 10 (maximum)
    function run(uint32 _amount, uint _maximum) public returns (uint256 requestId) {
        // Will revert if subscription is not set and funded.
        numWords = _amount;
        maximum = _maximum;
        requestId = COORDINATOR.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: keyHash,
                subId: s_subscriptionId,
                requestConfirmations: requestConfirmations,
                callbackGasLimit: callbackGasLimit,
                numWords: numWords,
                extraArgs: VRFV2PlusClient._argsToBytes(VRFV2PlusClient.ExtraArgsV1({nativePayment: false}))
            })
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, numWords);
        return requestId;      
    }

    function fulfillRandomWords(uint256 _requestId, uint256[] calldata _randomWords) internal override {
        require(s_requests[_requestId].exists, "request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;
        lastRandomWords = _randomWords;

        uint aux;
        for (uint i = 0; i < numWords; i++) {
            aux = _randomWords[i] % maximum + 1;
            raffleResult.push(aux);
        }
    }

}

